    function gtmAddToCart(data){
    if(typeof(data)=='undefined' || typeof(data.name)=='undefined'){
        return true ;
    }
    dataLayer.push({
        'event': 'addToCart',
        'ecommerce': {
            'currencyCode': 'CNY',
            'add': {
                'products': [{
                    'name': data.name,
                    'id':  data.id,
                    'price': data.price,
                    'brand': data.brand,
                    'category': data.category,
                    'dimension12':data.mall,
                    'metric1': data.metric1,
                    'dimension10': data.dimension10,
                    'dimension9': data.dimension9,
                    'dimension11': data.dimension11,
                    'dimension20': data.dimension20,
                    'dimension25': data.dimension25,
                    'dimension64':"yuanchuang",
                    'quantity': 1
                }]
            }
        }
    });
}

